able) {        echo "<tr>\n";        echo "<td colspan=\"3\">No Movies to Display</td>";        echo "</tr>\n";    }    else {        for ($i=0; $i<$rows; $i++) {            $row = $result->fetch_assoc();            echo    "<tr class=\"highlight\">";            echo    "<td>".($i+1)."</td>";            echo    "<td><a href=\"movie.php?name=".$row['name']."\" />".$row['name']."</a></td>";            echo    "<td>".$row['year']."</td>";            echo    "</tr>\n";        }    }    $result->free();    $moviesdb->close();}?></p><p><copyright>Roberto A. Flores. All Rights Reserved &copy; 2016</copyright></p></div></body></html>